import { addBorrow, delBorrow, getBorrow, listBorrow, updateBorrow } from '@/api/borrow/borrow'
// import borrowAvatar from './borrowAvatar'
export default {
  name: 'Config',
  // components: { borrowAvatar },
  data() {
    return {
      // fileList: [],
      // 遮罩层
      loading: true,
      // 选中数组
      ids: [],
      // 非单个禁用
      single: true,
      // 非多个禁用
      multiple: true,
      // 总条数
      total: 0,
      // 弹出层标题
      title: '',
      // 是否显示弹出层
      open: false,
      isEdit: false,
      // 类型数据字典
      typeOptions: [],
      borrowList: [],
      termTypeOptions: [], repayTypeOptions: [], borrowStatusOptions: [],
      // 查询参数
      queryParams: {
        pageIndex: 1,
        pageSize: 10,
        borrowName:
          undefined,
        interestRate:
          undefined,
        term:
          undefined,
        termType:
          undefined,
        amount:
          undefined,
        repayType:
          undefined,
        purpose:
          undefined,
        repayName:
          undefined,
        diya:
          undefined,
        amountLimit:
          undefined,
        borrowStatus:
          undefined

      },
      // 表单参数
      form: {
      },
      // 表单校验
      rules: {
        borrowName:
          [
            { required: true, message: '借款名称不能为空', trigger: 'blur' }
          ],
        interestRate:
          [
            { required: true, message: '利率不能为空', trigger: 'blur' }
          ],
        term:
          [
            { required: true, message: '借款期限不能为空', trigger: 'blur' }
          ],
        termType:
          [
            { required: true, message: '日期类型不能为空', trigger: 'blur' }
          ],
        amount:
          [
            { required: true, message: '借款金额不能为空', trigger: 'blur' }
          ],
        repayType:
          [
            { required: true, message: '还款方式不能为空', trigger: 'blur' }
          ],
        purpose:
          [
            { required: true, message: '借款目的不能为空', trigger: 'blur' }
          ],
        repayName:
          [
            { required: true, message: '还款人不能为空', trigger: 'blur' }
          ],
        diya:
          [
            { required: true, message: '抵押不能为空', trigger: 'blur' }
          ],
        amountLimit:
          [
            { required: true, message: '投资限额不能为空', trigger: 'blur' }
          ],
        borrowStatus:
          [
            { required: true, message: '借款状态不能为空', trigger: 'blur' }
          ]
      }
    }
  },
  created() {
    this.getList()
    this.getDicts('borrow_period').then(response => {
      this.termTypeOptions = response.data
    })

    this.getDicts('repay_type').then(response => {
      this.repayTypeOptions = response.data
    })

    this.getDicts('borrow_status').then(response => {
      this.borrowStatusOptions = response.data
    })
  },
  methods: {

    /** 查询参数列表 */
    getList() {
      this.loading = true
      listBorrow(this.addDateRange(this.queryParams, this.dateRange)).then(response => {
        this.borrowList = response.data.list
        this.total = response.data.count
        this.loading = false
      }
      )
    },
    // 取消按钮
    cancel() {
      this.open = false
      this.reset()
    },
    // 表单重置
    reset() {
      this.form = {

        id: undefined,
        borrowName: undefined,
        interestRate: undefined,
        term: undefined,
        termType: undefined,
        amount: undefined,
        repayType: undefined,
        purpose: undefined,
        repayName: undefined,
        diya: undefined,
        amountLimit: undefined,
        borrowStatus: undefined
      }
      this.resetForm('form')
    },
    termTypeFormat(row) {
      return this.selectDictLabel(this.termTypeOptions, row.termType)
    },
    repayTypeFormat(row) {
      return this.selectDictLabel(this.repayTypeOptions, row.repayType)
    },
    borrowStatusFormat(row) {
      return this.selectDictLabel(this.borrowStatusOptions, row.borrowStatus)
    },

    /** 搜索按钮操作 */
    handleQuery() {
      this.queryParams.pageIndex = 1
      this.getList()
    },
    /** 重置按钮操作 */
    resetQuery() {
      this.dateRange = []
      this.resetForm('queryForm')
      this.handleQuery()
    },
    /** 新增按钮操作 */
    handleAdd() {
      this.reset()
      this.open = true
      this.title = '添加Borrow'
      this.isEdit = false
    },
    // 多选框选中数据
    handleSelectionChange(selection) {
      this.ids = selection.map(item => item.id)
      this.single = selection.length !== 1
      this.multiple = !selection.length
    },
    /** 修改按钮操作 */
    handleUpdate(row) {
      this.reset()
      const id =
        row.id || this.ids
      getBorrow(id).then(response => {
        this.form = response.data
        this.open = true
        this.title = '修改Borrow'
        this.isEdit = true
      })
    },
    /** 提交按钮 */
    submitForm: function() {
      this.$refs['form'].validate(valid => {
        if (valid) {
          if (this.form.id !== undefined) {
            updateBorrow(this.form).then(response => {
              if (response.code === 200) {
                this.msgSuccess('修改成功')
                this.open = false
                this.getList()
              } else {
                this.msgError(response.msg)
              }
            })
          } else {
            addBorrow(this.form).then(response => {
              if (response.code === 200) {
                this.msgSuccess('新增成功')
                this.open = false
                this.getList()
              } else {
                this.msgError(response.msg)
              }
            })
          }
        }
      })
    },
    /** 删除按钮操作 */
    handleDelete(row) {
      const Ids = row.id || this.ids
      this.$confirm('是否确认删除编号为"' + Ids + '"的数据项?', '警告', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function() {
        return delBorrow(Ids)
      }).then(() => {
        this.getList()
        this.msgSuccess('删除成功')
      }).catch(function() {
      })
    }
  }
}
