import { addMembers, delMembers, getMembers, listMembers, updateMembers } from '@/api/members'

export default {
  name: 'Config',
  data() {
    return {
      // 遮罩层
      loading: true,
      // 选中数组
      ids: [],
      // 非单个禁用
      single: true,
      // 非多个禁用
      multiple: true,
      // 总条数
      total: 0,
      // 弹出层标题
      title: '',
      // 是否显示弹出层
      open: false,
      isEdit: false,
      // 类型数据字典
      typeOptions: [],
      membersList: [],

      // 查询参数
      queryParams: {
        pageIndex: 1,
        pageSize: 10,
        name:
          undefined,
        password:
          undefined,
        phone:
          undefined,
        email:
          undefined,
        realName:
          undefined,
        firstCardNum:
          undefined,
        lastCardNum:
          undefined,
        amountAll:
          undefined

      },
      // 表单参数
      form: {
      },
      // 表单校验
      rules: {
        name:
          [
            { required: true, message: '用户名不能为空', trigger: 'blur' }
          ],
        password:
          [
            { required: true, message: '密码不能为空', trigger: 'blur' }
          ],
        phone:
          [
            { required: true, message: '手机号不能为空', trigger: 'blur' }
          ],
        email:
          [
            { required: true, message: '邮箱不能为空', trigger: 'blur' }
          ],
        realName:
          [
            { required: true, message: '真实姓名不能为空', trigger: 'blur' }
          ],
        firstCardNum:
          [
            { required: true, message: '身份证首段不能为空', trigger: 'blur' }
          ],
        lastCardNum:
          [
            { required: true, message: '身份证尾号不能为空', trigger: 'blur' }
          ],
        amountAll:
          [
            { required: true, message: '总金额不能为空', trigger: 'blur' }
          ]
      }
    }
  },
  created() {
    this.getList()
  },
  methods: {
    /** 查询参数列表 */
    getList() {
      this.loading = true
      listMembers(this.addDateRange(this.queryParams, this.dateRange)).then(response => {
        this.membersList = response.data.list
        this.total = response.data.count
        this.loading = false
      }
      )
    },
    // 取消按钮
    cancel() {
      this.open = false
      this.reset()
    },
    // 表单重置
    reset() {
      this.form = {

        id: undefined,
        name: undefined,
        password: undefined,
        phone: undefined,
        email: undefined,
        realName: undefined,
        firstCardNum: undefined,
        lastCardNum: undefined,
        amountAll: undefined,
        amountFrozen: undefined,
        amountUsed: undefined
      }
      this.resetForm('form')
    },

    /** 搜索按钮操作 */
    handleQuery() {
      this.queryParams.pageIndex = 1
      this.getList()
    },
    /** 重置按钮操作 */
    resetQuery() {
      this.dateRange = []
      this.resetForm('queryForm')
      this.handleQuery()
    },
    /** 新增按钮操作 */
    handleAdd() {
      this.reset()
      this.open = true
      this.title = '添加Members'
      this.isEdit = false
    },
    // 多选框选中数据
    handleSelectionChange(selection) {
      this.ids = selection.map(item => item.id)
      this.single = selection.length !== 1
      this.multiple = !selection.length
    },
    /** 修改按钮操作 */
    handleUpdate(row) {
      this.reset()
      const id =
        row.id || this.ids
      getMembers(id).then(response => {
        this.form = response.data
        this.open = true
        this.title = '修改Members'
        this.isEdit = true
      })
    },
    /** 提交按钮 */
    submitForm: function() {
      this.$refs['form'].validate(valid => {
        if (valid) {
          if (this.form.id !== undefined) {
            updateMembers(this.form).then(response => {
              if (response.code === 200) {
                this.msgSuccess('修改成功')
                this.open = false
                this.getList()
              } else {
                this.msgError(response.msg)
              }
            })
          } else {
            addMembers(this.form).then(response => {
              if (response.code === 200) {
                this.msgSuccess('新增成功')
                this.open = false
                this.getList()
              } else {
                this.msgError(response.msg)
              }
            })
          }
        }
      })
    },
    /** 删除按钮操作 */
    handleDelete(row) {
      const Ids = row.id || this.ids
      this.$confirm('是否确认删除编号为"' + Ids + '"的数据项?', '警告', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function() {
        return delMembers(Ids)
      }).then(() => {
        this.getList()
        this.msgSuccess('删除成功')
      }).catch(function() {
      })
    }
  }
}
