import { addArea, delArea, getArea, listArea, updateArea } from '@/api/area'

export default {
  name: 'Config',
  data() {
    return {
      // 遮罩层
      loading: true,
      // 选中数组
      ids: [],
      // 非单个禁用
      single: true,
      // 非多个禁用
      multiple: true,
      // 总条数
      total: 0,
      // 弹出层标题
      title: '',
      // 是否显示弹出层
      open: false,
      isEdit: false,
      // 类型数据字典
      typeOptions: [],
      areaList: [],
      levelIdOptions: [],
      // 查询参数
      queryParams: {
        pageIndex: 1,
        pageSize: 10,
        areaId:
          undefined,
        areaName:
          undefined,
        parentId:
          undefined,
        levelId:
          undefined,
        postcode:
          undefined

      },
      // 表单参数
      form: {
      },
      // 表单校验
      rules: {
        areaId:
          [
            { required: true, message: '不能为空', trigger: 'blur' }
          ],
        areaName:
          [
            { required: true, message: '区域名称不能为空', trigger: 'blur' }
          ],
        parentId:
          [
            { required: true, message: '上级区域不能为空', trigger: 'blur' }
          ],
        levelId:
          [
            { required: true, message: '区域级别不能为空', trigger: 'blur' }
          ],
        postcode:
          [
            { required: true, message: '邮编不能为空', trigger: 'blur' }
          ]
      }
    }
  },
  created() {
    this.getList()
    this.getDicts('area_level').then(response => {
      this.levelIdOptions = response.data
    })
  },
  methods: {
    /** 查询参数列表 */
    getList() {
      this.loading = true
      listArea(this.addDateRange(this.queryParams, this.dateRange)).then(response => {
        this.areaList = response.data.list
        this.total = response.data.count
        this.loading = false
      }
      )
    },
    // 取消按钮
    cancel() {
      this.open = false
      this.reset()
    },
    // 表单重置
    reset() {
      this.form = {

        areaId: undefined,
        areaName: undefined,
        parentId: undefined,
        levelId: undefined,
        postcode: undefined
      }
      this.resetForm('form')
    },
    levelIdFormat(row) {
      return this.selectDictLabel(this.levelIdOptions, row.levelId)
    },

    /** 搜索按钮操作 */
    handleQuery() {
      this.queryParams.pageIndex = 1
      this.getList()
    },
    /** 重置按钮操作 */
    resetQuery() {
      this.dateRange = []
      this.resetForm('queryForm')
      this.handleQuery()
    },
    /** 新增按钮操作 */
    handleAdd() {
      this.reset()
      this.open = true
      this.title = '添加Area'
      this.isEdit = false
    },
    // 多选框选中数据
    handleSelectionChange(selection) {
      this.ids = selection.map(item => item.areaId)
      this.single = selection.length !== 1
      this.multiple = !selection.length
    },
    /** 修改按钮操作 */
    handleUpdate(row) {
      this.reset()
      const areaId =
        row.areaId || this.ids
      getArea(areaId).then(response => {
        this.form = response.data
        this.open = true
        this.title = '修改Area'
        this.isEdit = true
      })
    },
    /** 提交按钮 */
    submitForm: function() {
      this.$refs['form'].validate(valid => {
        if (valid) {
          if (this.form.areaId !== undefined) {
            updateArea(this.form).then(response => {
              if (response.code === 200) {
                this.msgSuccess('修改成功')
                this.open = false
                this.getList()
              } else {
                this.msgError(response.msg)
              }
            })
          } else {
            addArea(this.form).then(response => {
              if (response.code === 200) {
                this.msgSuccess('新增成功')
                this.open = false
                this.getList()
              } else {
                this.msgError(response.msg)
              }
            })
          }
        }
      })
    },
    /** 删除按钮操作 */
    handleDelete(row) {
      const Ids = row.areaId || this.ids
      this.$confirm('是否确认删除编号为"' + Ids + '"的数据项?', '警告', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function() {
        return delArea(Ids)
      }).then(() => {
        this.getList()
        this.msgSuccess('删除成功')
      }).catch(function() {
      })
    }
  }
}
