import { addInvest, delInvest, getInvest, listInvest, updateInvest } from '@/api/invest'
import { getBorrow } from '@/api/borrow/borrow'

export default {
  name: 'Config',
  data() {
    return {
      // 遮罩层
      loading: true,
      // 选中数组
      ids: [],
      // 非单个禁用
      single: true,
      // 非多个禁用
      multiple: true,
      // 总条数
      total: 0,
      // 弹出层标题
      title: '',
      // 是否显示弹出层
      open: false,
      isEdit: false,
      // 默认借款
      defaultBorrowName: '',
      // 类型数据字典
      typeOptions: [],
      investList: [],

      // 查询参数
      queryParams: {
        pageIndex: 1,
        pageSize: 10,
        investId:
          undefined,
        mamberId:
          undefined,
        investAmount:
          undefined,
        borrowId:
          undefined

      },
      // 表单参数
      form: {
      },
      // 表单校验
      rules: {
        investId:
          [
            { required: true, message: '投资id不能为空', trigger: 'blur' }
          ],
        mamberId:
          [
            { required: true, message: '用户id不能为空', trigger: 'blur' }
          ],
        investAmount:
          [
            { required: true, message: '投资金额不能为空', trigger: 'blur' }
          ],
        borrowId:
          [
            { required: true, message: '借款id不能为空', trigger: 'blur' }
          ]
      }
    }
  },
  created() {
    const borrowId = this.$route.params && this.$route.params.borrowId
    this.queryParams.borrowId = borrowId
    this.getBorrow(borrowId)
    this.getList()
  },
  methods: {
    getBorrow(borrowId) {
      this.loading = true
      getBorrow(borrowId).then(
        response => {
          this.queryParams.borrowId = response.data.borrowId
          this.defaultBorrowName = response.data.borrowName
        }
      )
    },
    /** 查询参数列表 */
    getList() {
      this.loading = true
      listInvest(this.addDateRange(this.queryParams, this.dateRange)).then(response => {
        this.investList = response.data.list
        this.total = response.data.count
        this.loading = false
      }
      )
    },
    // 取消按钮
    cancel() {
      this.open = false
      this.reset()
    },
    // 表单重置
    reset() {
      this.form = {

        investId: undefined,
        mamberId: undefined,
        investAmount: undefined,
        borrowId: undefined
      }
      this.resetForm('form')
    },

    /** 搜索按钮操作 */
    handleQuery() {
      this.queryParams.pageIndex = 1
      this.getList()
    },
    /** 重置按钮操作 */
    resetQuery() {
      this.dateRange = []
      this.resetForm('queryForm')
      this.handleQuery()
    },
    /** 新增按钮操作 */
    handleAdd() {
      this.reset()
      this.open = true
      this.title = '添加Invest'
      this.isEdit = false
    },
    // 多选框选中数据
    handleSelectionChange(selection) {
      this.ids = selection.map(item => item.investId)
      this.single = selection.length !== 1
      this.multiple = !selection.length
    },
    /** 修改按钮操作 */
    handleUpdate(row) {
      this.reset()
      const investId =
        row.investId || this.ids
      getInvest(investId).then(response => {
        this.form = response.data
        this.open = true
        this.title = '修改Invest'
        this.isEdit = true
      })
    },
    /** 提交按钮 */
    submitForm: function() {
      this.$refs['form'].validate(valid => {
        if (valid) {
          if (this.form.investId !== undefined) {
            updateInvest(this.form).then(response => {
              if (response.code === 200) {
                this.msgSuccess('修改成功')
                this.open = false
                this.getList()
              } else {
                this.msgError(response.msg)
              }
            })
          } else {
            addInvest(this.form).then(response => {
              if (response.code === 200) {
                this.msgSuccess('新增成功')
                this.open = false
                this.getList()
              } else {
                this.msgError(response.msg)
              }
            })
          }
        }
      })
    },
    /** 删除按钮操作 */
    handleDelete(row) {
      const Ids = row.investId || this.ids
      this.$confirm('是否确认删除编号为"' + Ids + '"的数据项?', '警告', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function() {
        return delInvest(Ids)
      }).then(() => {
        this.getList()
        this.msgSuccess('删除成功')
      }).catch(function() {
      })
    }
  }
}
